css:
:root{
      --heading: #0d6efd;
      --muted: #6c757d;
    }
    body { background: #f8f9fa; }
    .page {
      background: #fff;
      padding: 24px;
      border-radius: 12px;
      box-shadow: 0 6px 22px rgba(0,0,0,.06);
    }

    h1, h2, h3 { margin: 0; }
    h1 { font-size: 1.6rem; color: var(--heading); font-weight: 700; }
    .subtitle { color: var(--muted); font-size: .95rem; }

    .section-title {
      font-weight: 700; font-size: 1.05rem;
      border-left: 4px solid var(--heading);
      padding-left: .5rem; margin: 1.25rem 0 .75rem;
    }

    .kv .label { color: var(--muted); font-size: .9rem; }
    .kv .value { font-weight: 600; }

    /* Table jours (1..31) */
    .days-table { table-layout: fixed; }
    .days-table thead th {
      text-align: center;
      vertical-align: middle;
      font-size: .82rem;
      padding: .4rem .25rem;
      white-space: nowrap;
    }
    .days-table td {
      text-align: center;
      vertical-align: middle;
      padding: .45rem .25rem;
      font-size: .9rem;
    }
    .days-table th:first-child,
    .days-table td:first-child {
      text-align: left;
      width: 320px;
    }
    .days-table .day-col { width: 36px; }

    /* Signatures */
    .sig-box {
      height: 88px;
      border: 1px dashed #adb5bd;
      border-radius: 8px;
      background:
        repeating-linear-gradient(
          to bottom,
          transparent 0, transparent 17px,
          rgba(13,110,253,.06) 17px, rgba(13,110,253,.06) 18px
        );
    }
    .sig-meta { font-size: .9rem; color: var(--muted); }

    /* Impression A4 */
    @page { size: A4; margin: 14mm; }
    @media print {
      body { background: #fff; }
      .page { box-shadow: none; border-radius: 0; padding: 0; }
      .no-print { display: none !important; }
      a[href]:after { content: ""; }
    }
